// import { useEffect } from "react";
// import { useState } from "react";
import "../style.css";

export default function NavBar() {
  // console.log(`NAVBAR FUNCTION: ${window.location.pathname}`);
  return (
    <nav className="nav">
      <ul id="nav-menu">
        <CustomLink href="/">
          <b>HOME</b>
        </CustomLink>
        <CustomLink href="/compsci">
          <b>COMPSCI</b>
        </CustomLink>
        <CustomLink href="/photovideo">
          <b>CREATIVE</b>
        </CustomLink>
        <CustomLink href="/aboutme">
          <b>ABOUT ME</b>
        </CustomLink>
      </ul>
    </nav>
  );
}

function CustomLink({ href, children, ...props }) {
  // const [currentPath, setCurrentPath] = useState(window.location.pathname);

  // useEffect(() => {
  //   console.log(currentPath, "- Has changed through navbar");
  // }, [currentPath]);
  let currentPath = window.location.pathname;
  return (
    <li className={currentPath === href ? "active" : ""}>
      <a href={href} {...props}>
        {children}
      </a>
    </li>
  );
}
